import React from "react";
import styled from "styled-components";

function LandingBtn(){

    const Wrapper = styled.div`
        height: 100vh;
        width: 100%;
        display: flex;
        align-items: center;
        justify-content: center;
        flex-direction: column;
    `;

    const Btn = styled.svg`
        z-index: 4;
        position: relative;
        min-width: 325px;
        min-height: 325px;
        max-width: 800px;
        max-height: 800px;
        width:30vw;
        height:30vw;
        border-radius: 50%;
        border: 2px solid white;
        transition: all 1.5s ease-in-out;
        &:hover{
            transform: scale(0.8);

            cursor: pointer;
          
          .background {
            stroke: transparent;
          }
          
          .foreground {
            stroke-dashoffset: 0;
            transform: rotate(-90deg);
          }
          
        } 
    `;

    const Background = styled.ellipse`
        fill: transparent;
        stroke: #1abc9c;
        transition: all 200ms ease;
    `;

    const Foreground = styled.ellipse`
        fill: transparent;
        stroke-dasharray: 377;
        stroke-dashoffset: 377;
        stroke: #EE3769;
        transform-origin: 50% 50%;
        transform: rotate(-270deg);
        transition: all 800ms ease;
    `;

    const Text = styled.p`
        z-index: 5;
        color: white; 
        position: relative;
        top: 35%; 
        left: 50%; 
        transform: translate(-50%, -50%); 
        font-size: ${props => props.fontSize};
    `;

    return(
        <Wrapper>
            <Btn>
                <Background/>
                <Foreground/>
                <Text fontSize="calc(26px + 2.3vw)">
                    Lucas Vandermaarel
                </Text>
            </Btn>
        </Wrapper>
    );
}

export default LandingBtn;
