import React from "react";
import styled from "styled-components";

function LandingBtn(){

    const Btn = styled.span`
        z-index: 4;
        top: 15%;
        left:50%;
        transform: translateX(-50%);
        position: absolute;
        min-width: 325px;
        min-height: 325px;
        max-width: 800px;
        max-height: 800px;
        width:30vw;
        height:30vw;
        background-color: rgba(0, 0, 0, 0.66);
        border-radius: 50%;
        border: 2px solid white;
        transition: all .2s ease-in-out;
        :hover{
            transform: scale(0.7);
        } 
    `;

    let textStyle = {
        color: "white", 
        position: "relative", 
        top: "35%", 
        left: "50%", 
        transform: "translate(-50%, -50%)", 
        fontSize: "calc(26px + 2.3vw)"
    }

    return(
        <Btn>
            <p style={textStyle}>
                Lucas Vandermaarel
            </p>
        </Btn>
    );
}

export default LandingBtn;
