import React from "react";
import styled from "styled-components";

function LandingBtn(){

    const Btn = styled.span`
        z-index: 4;
        top: 15%;
        position: absolute;
        min-width: 325px;
        min-height: 325px;
        max-width: 800px;
        max-height: 800px;
        width:30vw;
        height:30vw;
        background-color: rgba(0, 0, 0, 0.66);
        border-radius: 50%;
        border: 2px solid white;
        transition: all 1.2s ease-in-out;
        :hover{
            transform: scale(0.8);
        } 
    `;


    const Text = styled.p`
        color: white; 
        position: relative;
        top: 35%; 
        left: 50%; 
        transform: translate(-50%, -50%); 
        font-size: ${props => props.fontSize}
    `;



    return(
        <Btn>
            <Text fontSize="fontSize: 5vw">
                Lucas Vandermaarel
            </Text>
        </Btn>
    );
}

export default LandingBtn;
