import logo from './logo.svg';
import './App.css';
import {Button} from '@material-ui/core';
import {BrowserRouter as Router, Switch, Route, Link} from "react-router-dom";
import About from './components/About';
import Work from './components/Work'; 
import mobileLanding from './img/mobileLanding.jpg';
import { Typography } from '@material-ui/core';
import Particles from 'react-particles-js';

//TODO: make buttons the same width, resize with same scale
//possible add particle effect
//drawer navigation links 

function App() {
  const imgUrl = window.innerWidth >=650 ? mobileLanding : mobileLanding;
  return (
    <Router>
        <Switch>
          <Route path="/work">
            <Work />
          </Route>
          <Route path="/about">
            <About />
          </Route>
          <Route path="/">

          
          <div className="App">
         
           
            < Particles
            params = {
              {
                particles: {
                  line_linked: {
                    shadow: {
                      enable: true,
                      color: "#3CA9D1",
                      blur: 5
                    }
                  }
                }
              }
            }
            style = {
              {
                width: '100%',
                background: "#101119"
              }
            }
            />
     
              <span style={{position: "relative", minWidth: "325px", minHeight: "325px", width:"30vw", height:"30vw", backgroundColor: "#000000", borderRadius:"50%"}}>
              <p style={{position: "absolute", top: "50%", left: "50%", transform: "translate(-50%, -100%)", fontSize: "calc(24px + 2.3vw)"}}>
                Lucas Vandermaarel
              </p>
              </span>
             

           
            </div>
          </Route>
        </Switch>
    </Router>
    
  );

  /* 
  <div style={{display: "inline-block"}}>
      <Button variant="contained" id="workBtn"><Link style={{textDecoration: "none", color: "#ccc"}} to="/work">View Work</Link></Button>
      <Button variant="contained" id="aboutBtn"><Link style={{textDecoration: "none", color: "#ccc"}} to="/about">About Me</Link></Button>
  </div>

              */
}

export default App;
